h1.style:
display: inline-block;
position: absolute;
animation: scroll 10s linear infinite, bounce 0.8s alternate infinite;
font-family: 'Comic Sans MS', cursive;
font-weight: bold;
font-style: italic;
color: hotpink;
font-size: 1.5em;

h2.style:
display: inline-block;
position: absolute;
animation: scroll 12s linear infinite, bounce 1s alternate infinite;
animation-delay: 1s;
font-family: 'Comic Sans MS', cursive;
font-weight: bold;
font-style: italic;
color: blue;
font-size: 2em;
top: 40px;

h3.style:
display: inline-block;
position: absolute;
animation: scroll 8s linear infinite, bounce 1.2s alternate infinite;
animation-delay: 2s;
font-family: 'Times New Roman', serif;
font-weight: bold;
font-size: 1.3em;
top: 80px;
